import { useEffect, useState } from 'react';
import './App.css';

function App() {


  let [articles,setArticles] = useState([])

   useEffect(()=>{

     fetch (" https://newsapi.org/v2/everything?q=microsoft,&from=2023-12-10&apiKey=f0e0343d4dea4dd895847e4395c81fc5")
     .then((Response)=>Response.json())
     .then((news)=>{
       setArticles(news.articles);
     })

   },[])

  return (
    <div className="App">
      <h1>News App</h1>
    </div>
  );
  }

export default App;
