import { useEffect, useState } from 'react';
 import News from './News';
import './App.css';

function App() {


  let [articles,setArticles] = useState([])

   useEffect(()=>{

     fetch (" https://newsapi.org/v2/everything?q=microsoft,&from=2023-12-10&apiKey=f0e0343d4dea4dd895847e4395c81fc5")
     .then((Response)=>Response.json())
     .then((news)=>{
       setArticles(news.articles);
     })

   },[])

  return (
    <div className="App">

      <header className='Header'>
       <h1>NEWS 07</h1>
       <input type='text' placeholder='Search'/>
      </header>
     <section className='News-articles'>

      {
        articles.map
      }

       <News />



    </section>

    </div>
  );
  }

export default App;
